            var intVal = function(i) {
                return typeof i === 'string' ?
                    i.replace(/[\$,]/g, '') * 1 :
                    typeof i === 'number' ?
                    i :
                    0;
            };

            totalIncomes = api
                .rows({filter:'applied'})
                .data()
                .filter( function ( value, index ) {
                    return value[3] == 'INGRESO' ? true : false;
                } )
                .pluck(5)
                .reduce(function (a, b) {
                    return intVal(a) + intVal(b);
                }, 0);
